INSERT INTO wp_posts VALUES('31', '1', '2014-05-26 11:13:15', '2014-05-26 11:13:15', '<h3 style=\"text-align: justify;\">Our Cultural activities</h3>\r\n<p style=\"text-align: justify;\">NSTU Pharmacy is very conscious to preserve Bangladesh\'s culture. It celebrates International Mother Language Day, Pahela Baishak, Bosonta Baron, Independence Day, Victory Day, International Anti Drug Day, Friendship Day and other national and international days with actual manner. Cultural competition occurs here every year. This competition includes songs, dance, recitation, etc. NSTU Pharmacy is proud of its Cultural Club and Debating Society. Many students of our family are involved here.</p>\r\n<p style=\"text-align: justify;\"><a href=\"http://www.nstupharma.org/photos/6-676-x-507/\"><img class=\"alignnone wp-image-277\" src=\"http://www.nstupharma.org/wp-content/uploads/2014/06/6-676-x-507-300x225.jpg\" alt=\"6 (676 x 507)\" width=\"500\" height=\"338\" /></a></p>\r\n&nbsp;\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: justify;\">Sports</h3>\r\n<p style=\"text-align: justify;\">Our family has strong cricket, football, volleyball and badminton team. Interdepartmental sport competition is held regularly indoors and outdoors.</p>\r\n<p style=\"text-align: justify;\"><a href=\"http://www.nstupharma.org/students/cultural-activities/1016191_10201600324235665_877172970_n/\"><img class=\"alignright wp-image-407\" src=\"http://www.nstupharma.org/wp-content/uploads/2014/05/1016191_10201600324235665_877172970_n-300x225.jpg\" alt=\"1016191_10201600324235665_877172970_n\" width=\"500\" height=\"375\" /></a></p>', 'Cultural Activities', '', 'publish', 'open', 'open', '', 'cultural-activities', '', '', '2014-07-15 05:23:19', '2014-07-15 05:23:19', '', '27', 'http://www.nstupharma.org/?page_id=31', '0', 'page', '', '0');
INSERT INTO wp_posts VALUES('33', '1', '2014-05-26 11:13:34', '2014-05-26 11:13:34', '<p style=\"text-align: justify;\">NSTU Pharmacy offers scholarships to its meritorious students. Poor and meritorious students receive awards. In every term, five students in each batch of our department are offered scholarships. Department provides scholarships regularly.</p>', 'Scholarships', '', 'publish', 'open', 'open', '', 'scholarships', '', '', '2014-07-12 14:57:43', '2014-07-12 14:57:43', '', '27', 'http://www.nstupharma.org/?page_id=33', '0', 'page', '', '0');
