INSERT INTO wp_posts VALUES('45', '1', '2014-05-26 11:17:03', '2014-05-26 11:17:03', '<h3 style=\"text-align: justify;\"><strong>How to apply:</strong></h3>\r\n<p style=\"text-align: justify;\">Applicants have to collect &amp; submit application form from the Departmental office within the deadline.</p>\r\n<p style=\"text-align: justify;\">Applicants have to attach all necessary documents with application form:</p>\r\n\r\n<ol style=\"text-align: justify;\">\r\n	<li>Attested photocopies of academic grade sheets/ mark sheets and certificates;</li>\r\n	<li>Certificate from final Head of the Institution / Head of the Department.</li>\r\n	<li>Two copies passport size photographs.</li>\r\n</ol>\r\n&nbsp;\r\n<p style=\"text-align: justify;\">Application form is collected by cash/ bank Draft to “application form sell account no. 7494, Agrani Bank Limited, NSTU\" from related Department Office.</p>\r\n<p style=\"text-align: justify;\">N.B: Service holder candidates must submit certificates of 18 months’ vacation from their own employers.</p>\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h3><strong>Admission Requirements:</strong></h3>\r\n<ol style=\"text-align: justify;\">\r\n	<li>Applicants must have Bachelor (Hons) degree in related subjects from Noakhali Science and Technology University (NSTU) or any Public University/ Institution.</li>\r\n	<li>Applicants must have minimum GPA 2.50 in Bachelor/ Equivalent level.</li>\r\n</ol>\r\n&nbsp;\r\n\r\n&nbsp;\r\n<h3 style=\"text-align: justify;\"><strong>Application fee:</strong></h3>\r\n<p style=\"text-align: justify;\">The application fee for the Masters’ degree course is 500 BDT.</p>\r\n\r\n<h3 style=\"text-align: justify;\"></h3>\r\n<h3 style=\"text-align: center;\"></h3>\r\n&nbsp;\r\n<h3 style=\"text-align: center;\"><strong><a href=\"http://www.nstupharma.org/wp-content/uploads/2014/05/Masters-admission-notice-for-session-2012-13.pdf\">Master\'s admission notice for session 2012-13</a></strong></h3>\r\n&nbsp;\r\n\r\n&nbsp;', 'Graduates admission', '', 'publish', 'open', 'open', '', 'graduates-admission', '', '', '2014-07-12 14:53:11', '2014-07-12 14:53:11', '', '43', 'http://www.nstupharma.org/?page_id=45', '0', 'page', '', '0');
