CREATE TABLE `wp_options` (  `option_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `option_name` varchar(64) NOT NULL DEFAULT '',  `option_value` longtext NOT NULL,  `autoload` varchar(20) NOT NULL DEFAULT 'yes',  PRIMARY KEY (`option_id`),  UNIQUE KEY `option_name` (`option_name`)) ENGINE=InnoDB AUTO_INCREMENT=7417 DEFAULT CHARSET=utf8;
