CREATE TABLE `wp_posts` (  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `post_content` longtext NOT NULL,  `post_title` text NOT NULL,  `post_excerpt` text NOT NULL,  `post_status` varchar(20) NOT NULL DEFAULT 'publish',  `comment_status` varchar(20) NOT NULL DEFAULT 'open',  `ping_status` varchar(20) NOT NULL DEFAULT 'open',  `post_password` varchar(20) NOT NULL DEFAULT '',  `post_name` varchar(200) NOT NULL DEFAULT '',  `to_ping` text NOT NULL,  `pinged` text NOT NULL,  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `post_content_filtered` longtext NOT NULL,  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',  `guid` varchar(255) NOT NULL DEFAULT '',  `menu_order` int(11) NOT NULL DEFAULT '0',  `post_type` varchar(20) NOT NULL DEFAULT 'post',  `post_mime_type` varchar(100) NOT NULL DEFAULT '',  `comment_count` bigint(20) NOT NULL DEFAULT '0',  PRIMARY KEY (`ID`),  KEY `post_name` (`post_name`),  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),  KEY `post_parent` (`post_parent`),  KEY `post_author` (`post_author`)) ENGINE=InnoDB AUTO_INCREMENT=538 DEFAULT CHARSET=utf8;
CREATE TABLE `wp_signups` (  `signup_id` bigint(20) NOT NULL AUTO_INCREMENT,  `domain` varchar(200) NOT NULL DEFAULT '',  `path` varchar(100) NOT NULL DEFAULT '',  `title` longtext NOT NULL,  `user_login` varchar(60) NOT NULL DEFAULT '',  `user_email` varchar(100) NOT NULL DEFAULT '',  `registered` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `activated` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `active` tinyint(1) NOT NULL DEFAULT '0',  `activation_key` varchar(50) NOT NULL DEFAULT '',  `meta` longtext,  PRIMARY KEY (`signup_id`),  KEY `activation_key` (`activation_key`),  KEY `user_email` (`user_email`),  KEY `user_login_email` (`user_login`,`user_email`),  KEY `domain_path` (`domain`,`path`)) ENGINE=InnoDB AUTO_INCREMENT=117 DEFAULT CHARSET=utf8;
CREATE TABLE `wp_term_relationships` (  `object_id` bigint(20) unsigned NOT NULL DEFAULT '0',  `term_taxonomy_id` bigint(20) unsigned NOT NULL DEFAULT '0',  `term_order` int(11) NOT NULL DEFAULT '0',  PRIMARY KEY (`object_id`,`term_taxonomy_id`),  KEY `term_taxonomy_id` (`term_taxonomy_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `wp_term_taxonomy` (  `term_taxonomy_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `term_id` bigint(20) unsigned NOT NULL DEFAULT '0',  `taxonomy` varchar(32) NOT NULL DEFAULT '',  `description` longtext NOT NULL,  `parent` bigint(20) unsigned NOT NULL DEFAULT '0',  `count` bigint(20) NOT NULL DEFAULT '0',  PRIMARY KEY (`term_taxonomy_id`),  UNIQUE KEY `term_id_taxonomy` (`term_id`,`taxonomy`),  KEY `taxonomy` (`taxonomy`)) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;
CREATE TABLE `wp_terms` (  `term_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `name` varchar(200) NOT NULL DEFAULT '',  `slug` varchar(200) NOT NULL DEFAULT '',  `term_group` bigint(10) NOT NULL DEFAULT '0',  PRIMARY KEY (`term_id`),  UNIQUE KEY `slug` (`slug`),  KEY `name` (`name`)) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;
CREATE TABLE `wp_uam_accessgroup_to_object` (  `object_id` varchar(11) NOT NULL,  `object_type` varchar(255) NOT NULL,  `group_id` int(11) NOT NULL,  PRIMARY KEY (`object_id`,`object_type`,`group_id`)) ENGINE=InnoDB DEFAULT CHARSET=utf8;
CREATE TABLE `wp_uam_accessgroups` (  `ID` int(11) NOT NULL AUTO_INCREMENT,  `groupname` tinytext NOT NULL,  `groupdesc` text NOT NULL,  `read_access` tinytext NOT NULL,  `write_access` tinytext NOT NULL,  `ip_range` mediumtext,  PRIMARY KEY (`ID`)) ENGINE=InnoDB AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;
CREATE TABLE `wp_usermeta` (  `umeta_id` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `user_id` bigint(20) unsigned NOT NULL DEFAULT '0',  `meta_key` varchar(255) DEFAULT NULL,  `meta_value` longtext,  PRIMARY KEY (`umeta_id`),  KEY `user_id` (`user_id`),  KEY `meta_key` (`meta_key`)) ENGINE=InnoDB AUTO_INCREMENT=1812 DEFAULT CHARSET=utf8;
CREATE TABLE `wp_users` (  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,  `user_login` varchar(60) NOT NULL DEFAULT '',  `user_pass` varchar(64) NOT NULL DEFAULT '',  `user_nicename` varchar(50) NOT NULL DEFAULT '',  `user_email` varchar(100) NOT NULL DEFAULT '',  `user_url` varchar(100) NOT NULL DEFAULT '',  `user_registered` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',  `user_activation_key` varchar(60) NOT NULL DEFAULT '',  `user_status` int(11) NOT NULL DEFAULT '0',  `display_name` varchar(250) NOT NULL DEFAULT '',  PRIMARY KEY (`ID`),  KEY `user_login_key` (`user_login`),  KEY `user_nicename` (`user_nicename`)) ENGINE=InnoDB AUTO_INCREMENT=122 DEFAULT CHARSET=utf8;
